import React from 'react'
import styles from './registrationForm.css'
import { Formik } from 'formik';
import userIcon from '../assets/userIcon.png'
import Input from '../UI/Input/Input';

export default function RegistrationForm() {
    return (
    <div className='registrationFormContainer'>
            {/* <h1 className='registrationFormTitle'>Create Account</h1> */}
        <Formik
            initialValues={{ email: '', password: '' }}
            validate={values => {
                const errors = {};
                if (!values.email) {
                    errors.email = 'Required';
                } else if (
                    !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
                ) {
                    errors.email = 'Invalid email address';
                }
                return errors;
            }}
            onSubmit={(values, { setSubmitting }) => {
                setTimeout(() => {
                    alert(JSON.stringify(values, null, 2));
                    setSubmitting(false);
                }, 400);
            }}
        >
            {({
                values,
                errors,
                touched,
                handleChange,
                handleBlur,
                handleSubmit,
                isSubmitting,
                /* and other goodies */
                }) => (
                    <div className="">
                        <form onSubmit={handleSubmit}>
                            <div className='registrationFormInputContainer'>
                                <input
                                    type="email"
                                    name="email"
                                    onChange={handleChange}
                                    onBlur={handleBlur}
                                    value={values.email}
                                    placeholder='Your name'
                                    className='registrationFormInput'
                                />
                            </div>
                            {errors.email && touched.email && errors.email}
                            <div className='registrationFormInputContainer'>
                                <img  />
                                <Input
                                    src={userIcon}
                                    alt="userIcon"
                                    type="password"
                                    name="password"
                                    onChange={handleChange}
                                    onBlur={handleBlur}
                                    value={values.password}
                                    placeholder='Your last name'

                                />
                            </div>
                            {errors.password && touched.password && errors.password}
                            <div className="registrationFormButtonContainer">
                                <button type="submit" disabled={isSubmitting} className='registrationFormButton'>
                                    Submit
                                </button>
                            </div>
                        </form>
                    </div>
            )}
        </Formik>
    </div>
    )
}
