import React from 'react';
import './registrationForm.css';
import { Formik } from 'formik';
import userIcon from '../assets/userIcon.png';
import Button from '../UI/Button/Button';

export default function RegistrationForm() {
    return (
        <div className='registrationFormContainer'>
            <Formik
                initialValues={{
                    firstName: '',
                    lastName: '',
                    email: '',
                    password: '',
                    confirmPassword: ''
                }}
                validate={values => {
                    const errors = {};
                    
                   

                    return errors;
                }}
                onSubmit={(values, { setSubmitting }) => {
                    setTimeout(() => {
                        alert(JSON.stringify(values, null, 2));
                        setSubmitting(false);
                    }, 400);
                }}
            >
                {({
                    values,
                    errors,
                    touched,
                    handleChange,
                    handleBlur,
                    handleSubmit,
                    isSubmitting,
                }) => (
                    <form onSubmit={handleSubmit}>
                        <div className='registrationFormInputContainer'>
                            <img className="inputIcon" src={userIcon} alt="User Icon" />
                            <input
                                type="text"
                                name="firstName"
                                onChange={handleChange}
                                onBlur={handleBlur}
                                value={values.firstName}
                                placeholder="Your name"
                                className='registrationFormInput'
                            />
                        </div>
                        {errors.firstName && touched.firstName && <div className="error">{errors.firstName}</div>}

                        <div className='registrationFormInputContainer'>
                            <img className="inputIcon" src={userIcon} alt="User Icon" />
                            <input
                                type="text"
                                name="lastName"
                                onChange={handleChange}
                                onBlur={handleBlur}
                                value={values.lastName}
                                placeholder="Your last name"
                                className='registrationFormInput'
                            />
                        </div>
                        {errors.lastName && touched.lastName && <div className="error">{errors.lastName}</div>}

                        <div className='registrationFormInputContainer'>
                            <img className="inputIcon" src={userIcon} alt="User Icon" />
                            <input
                                type="email"
                                name="email"
                                onChange={handleChange}
                                onBlur={handleBlur}
                                value={values.email}
                                placeholder="Your email"
                                className='registrationFormInput'
                            />
                        </div>
                        {errors.email && touched.email && <div className="error">{errors.email}</div>}

                        <div className='registrationFormInputContainer'>
                            <img className="inputIcon" src={userIcon} alt="User Icon" />
                            <input
                                type="password"
                                name="password"
                                onChange={handleChange}
                                onBlur={handleBlur}
                                value={values.password}
                                placeholder="Password"
                                className='registrationFormInputPassword'
                            />
                        {errors.password && touched.password && <div className="error">{errors.password}</div>}

                            <img className="inputIcon" src={userIcon} alt="User Icon" />
                            <input
                                type="password"
                                name="confirmPassword"
                                onChange={handleChange}
                                onBlur={handleBlur}
                                value={values.confirmPassword}
                                placeholder="Password confirmation"
                                className='registrationFormInputPassword'
                            />
                        </div>
                        {errors.confirmPassword && touched.confirmPassword && <div className="error">{errors.confirmPassword}</div>}

                        <div className="registrationFormButtonContainer">
                            <button type="submit" disabled={isSubmitting} className='registrationFormButton'>
                                SING UP
                            </button>
                        </div>
                    </form>
                )}
            </Formik>
        </div>
    );
}
