import React from 'react';
import './registrationForm.css';
import { Formik } from 'formik';
import userIcon from '../assets/userIcon.png';
import Button from '../UI/Button/Button';
import Input from '../UI/Input/Input';

export default function RegistrationForm() {
    return (
        <div className='registrationFormContainer'>
            <Formik
                initialValues={{
                    firstName: '',
                    lastName: '',
                    email: '',
                    password: '',
                    confirmPassword: ''
                }}
                onSubmit={(values, { setSubmitting }) => {
                    setTimeout(() => {
                        alert(JSON.stringify(values, null, 2));
                        setSubmitting(false);
                    }, 400);
                }}
            >
                {({
                    values,
                    errors,
                    touched,
                    handleChange,
                    handleBlur,
                    handleSubmit,
                    isSubmitting,
                }) => (
                    <form onSubmit={handleSubmit}>
                        {/* Ім'я */}
                        <Input
                            type="text"
                            name="firstName"
                            onChange={handleChange}
                            placeholder="Your name"
                            value={values.firstName}
                            src={userIcon}
                            alt="User Icon"
                            inputClass="registrationFormInput"
                            wrapperClass="registrationFormInputContainer"
                        />
                        {errors.firstName && touched.firstName && <div className="error">{errors.firstName}</div>}

                        {/* Прізвище */}
                        <Input
                            type="text"
                            name="lastName"
                            onChange={handleChange}
                            placeholder="Your last name"
                            value={values.lastName}
                            src={userIcon}
                            alt="User Icon"
                            inputClass="registrationFormInput"
                            wrapperClass="registrationFormInputContainer"
                        />
                        {errors.lastName && touched.lastName && <div className="error">{errors.lastName}</div>}

                        {/* Email */}
                        <Input
                            type="email"
                            name="email"
                            onChange={handleChange}
                            placeholder="Your email"
                            value={values.email}
                            src={userIcon}
                            alt="User Icon"
                            inputClass="registrationFormInput"
                            wrapperClass="registrationFormInputContainer"
                        />
                        {errors.email && touched.email && <div className="error">{errors.email}</div>}

                        {/* Пароль та підтвердження */}
                        <div className="registrationFormInputContainerPass">
                            <Input
                                type="password"
                                name="password"
                                onChange={handleChange}
                                placeholder="Password"
                                value={values.password}
                                src={userIcon}
                                alt="User Icon"
                                inputClass="registrationFormInputPassword"
                                wrapperClass="registrationFormInputContainer"
                            />
                            <Input
                                type="password"
                                name="confirmPassword"
                                onChange={handleChange}
                                placeholder="Password confirmation"
                                value={values.confirmPassword}
                                src={userIcon}
                                alt="User Icon"
                                inputClass="registrationFormInputPassword"
                                wrapperClass="registrationFormInputContainer"
                            />
                        </div>
                        {errors.confirmPassword && touched.confirmPassword && <div className="error">{errors.confirmPassword}</div>}

                        {/* Кнопка реєстрації */}
                        <div className="registrationFormButtonContainer">
                            <Button type="submit" disabled={isSubmitting} className='registrationFormButton'>
                                SIGN UP
                            </Button>
                        </div>
                    </form>
                )}
            </Formik>
        </div>
    );
}
